(9,98,'service-user-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    sentile:\r\n      hostname: localhost\r\n      port: 8020\r\n\r\nspring:\r\n  application:\r\n    name: service-user\r\n  main:\r\n    allow-bean-definition-overriding: true\r\n  jackson:\r\n    time-zone: GMT+8\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n  cloud:\r\n    nacos:\r\n      discovery:\r\n        server-addr: 182.92.153.190:8848\r\n      config:\r\n        namespace: fb409926-dcc5-4426-945f-5d1c5cdf3813\r\n    sentinel:\r\n      transport:\r\n        dashboard: ${base.config.sentile.hostname}:${base.config.sentile.port}\r\n\r\nsecurity:\r\n  oauth2:\r\n    client:\r\n      client-id: client\r\n      client-secret: secret\r\n      access-token-uri: http://localhost:9001/oauth/token\r\n      user-authorization-uri: http://localhost:9001/oauth/authorize\r\n    resource:\r\n      token-info-uri: http://localhost:9001/oauth/check_token\r\ndubbo:\r\n  customer:\r\n    timeout: 5000\r\n  scan:\r\n    base-packages: com.kevin.cloud.service.controller\r\n  protocol:\r\n    name: dubbo\r\n    port: -1\r\n    serialization: kryo\r\n  registry:\r\n    address: nacos://182.92.153.190\r\n    port: 8848\r\n    \r\nfeign:\r\n  client:\r\n    config:\r\n      default:\r\n        connectTimeout: 1000 # 相当于Request.Optionsn 连接超时时间\r\n        readTimeout: 5000     # 相当于Request.Options 读取超时时间\r\n        loggerLevel: full     # 配置Feign的日志级别，相当于代码配置方式中的Logger\r\n  sentinel:\r\n    enabled: true\r\nserver:\r\n  # ${random.int[9000,9999]} 随机端口\r\n  port: 9000\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"','b5bea16781bc11fd5fe8cd1b902ac058','2010-05-05 00:00:00','2020-02-20 17:41:05',NULL,'47.240.91.132','U','fb409926-dcc5-4426-945f-5d1c5cdf3813'),
(0,99,'gateway-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    nacos:\r\n      hostname: 182.92.153.190\r\n      port: 8848\r\n\r\nspring:\r\n  application:\r\n    # 应用名称\r\n    name: gateway\r\n  main:\r\n    allow-bean-definition-overriding: true\r\n  cloud:\r\n    # 使用 Nacos 作为服务注册发现\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${base.config.nacos.hostname}:${base.config.nacos.port}\r\n    # 路由网关配置\r\n    gateway:\r\n      # 设置与服务注册发现组件结合，这样可以采用服务名的路由策略\r\n      discovery:\r\n        locator:\r\n          enabled: true\r\n      # 配置路由规则\r\n      routes:\r\n        # 采用自定义路由 ID（有固定用法，不同的 id 有不同的功能，详见：\r\n        #https://cloud.spring.io/spring-cloud-gateway/2.0.x/single/spring-cloud-gateway.html#gateway-route-filters）\r\n        - id: SERVICE-OAUTH2\r\n          # 采用 LoadBalanceClient 方式请求，以 lb:// 开头，后面的是注册在 Nacos 上的服务名\r\n          # 这里的 在nacos中的服务名，最好不要用下划线的服务名，我遇到了这个坑，用下划线找不到\r\n          uri: lb://service-oauth2\r\n          # Predicate 翻译过来是“谓词”的意思，必须，主要作用是匹配用户的请求，有很多种用法\r\n          predicates:\r\n            # 路径匹配，以 api 开头，直接配置是不生效的，看 filters 配置\r\n            - Path=/api/oauth/**\r\n          filters:\r\n            # 前缀过滤，默认配置下，我们的请求路径是 http://localhost:8888/kevin-cloud-oauth2/** 这时会路由到指定的服务\r\n            # 此处配置去掉 1 个路径前缀，再配置上面的 Path=/api/**，就能按照 http://localhost:8888/api/** 的方式访问了\r\n            - StripPrefix=1\r\n        - id: SERVICE-USER\r\n          uri: lb://service-user\r\n          predicates:\r\n            - Path=/api/user/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-UPLOAD\r\n          uri: lb://cloud-upload\r\n          predicates:\r\n            - Path=/api/upload/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-MESSAGE\r\n          uri: lb://cloud-message\r\n          predicates:\r\n            - Path=/api/message/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-BLOG\r\n          uri: lb://service-blog\r\n          predicates:\r\n            - Path=/api/blog/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-ARTICLE\r\n          uri: lb://service-article\r\n          predicates:\r\n            - Path=/api/article/**\r\n          filters:\r\n            - StripPrefix=1\r\n\r\nserver:\r\n  port: 8888\r\n\r\n# 配置日志级别，方别调试\r\nlogging:\r\n  level:\r\n    org.springframework.cloud.gateway: debug','eb037ac20e9a33ce4a5aab675a3d6e6d','2010-05-05 00:00:00','2020-02-21 14:51:03',NULL,'47.240.91.132','I','fb409926-dcc5-4426-945f-5d1c5cdf3813'),
