(0,152,'gateway-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    nacos:\r\n      hostname: ali.sizegang.cn\r\n      port: 8848\r\n      namespace: 270403d1-0aed-4ed7-8643-492a39d9c6d6\r\n\r\nspring:\r\n\r\n  cloud:\r\n    # 路由网关配置\r\n    gateway:\r\n      # 设置与服务注册发现组件结合，这样可以采用服务名的路由策略\r\n      discovery:\r\n        locator:\r\n          enabled: true\r\n      # 配置路由规则\r\n      routes:\r\n        # 采用自定义路由 ID（有固定用法，不同的 id 有不同的功能，详见：\r\n        #https://cloud.spring.io/spring-cloud-gateway/2.0.x/single/spring-cloud-gateway.html#gateway-route-filters）\r\n        - id: SERVICE-OAUTH2\r\n          # 采用 LoadBalanceClient 方式请求，以 lb:// 开头，后面的是注册在 Nacos 上的服务名\r\n          # 这里的 在nacos中的服务名，最好不要用下划线的服务名，我遇到了这个坑，用下划线找不到\r\n          uri: lb://service-oauth2\r\n          # Predicate 翻译过来是“谓词”的意思，必须，主要作用是匹配用户的请求，有很多种用法\r\n          predicates:\r\n            # 路径匹配，以 api 开头，直接配置是不生效的，看 filters 配置\r\n            - Path=/api/oauth/**\r\n          filters:\r\n            # 前缀过滤，默认配置下，我们的请求路径是 http://localhost:8888/kevin-cloud-oauth2/** 这时会路由到指定的服务\r\n            # 此处配置去掉 1 个路径前缀，再配置上面的 Path=/api/**，就能按照 http://localhost:8888/api/** 的方式访问了\r\n            - StripPrefix=1\r\n        - id: SERVICE-USER\r\n          uri: lb://service-user\r\n          predicates:\r\n            - Path=/api/user/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-UPLOAD\r\n          uri: lb://cloud-upload\r\n          predicates:\r\n            - Path=/api/upload/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-MESSAGE\r\n          uri: lb://cloud-message\r\n          predicates:\r\n            - Path=/api/message/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-BLOG\r\n          uri: lb://service-blog\r\n          predicates:\r\n            - Path=/api/blog/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-ARTICLE\r\n          uri: lb://service-article\r\n          predicates:\r\n            - Path=/api/article/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # 使用 Nacos 作为服务注册发现\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${base.config.nacos.hostname}:${base.config.nacos.port}\r\n        namespace: ${base.config.nacos.namespace}\r\n      config:\r\n        namespace: ${base.config.nacos.namespace}\r\n  application:\r\n    # 应用名称\r\n    name: gateway\r\n\r\nserver:\r\n  port: 8888\r\n\r\n# 配置日志级别，方别调试\r\nlogging:\r\n  level:\r\n    org.springframework.cloud.gateway: debug','ee83bbc70d96673a5c2e82ab3f880881','2010-05-05 00:00:00','2020-02-22 14:41:57',NULL,'47.240.91.132','I','270403d1-0aed-4ed7-8643-492a39d9c6d6'),
(0,153,'provider-redis-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    nacos:\r\n      hostname: ali.sizegang.cn\r\n      port: 8848\r\n      namespace: 270403d1-0aed-4ed7-8643-492a39d9c6d6\r\n    redis:\r\n      hostname: www.sizegang.cn\r\n      port: 16379\r\n\r\nspring:\r\n  application:\r\n    name: provider-redis\r\n  main:\r\n    allow-bean-definition-overriding: true\r\n  redis:\r\n    host: ${base.config.redis.hostname}\r\n    port: ${base.config.redis.port}\r\n    lettuce:\r\n      pool:\r\n        # 连接池中的最大空闲连接\r\n        max-idle: 30\r\n        # 连接池最大连接数（使用负值表示没有限制）\r\n        max-active: 8\r\n        # 连接池最大阻塞等待时间（使用负值表示没有限制）\r\n        max-wait: -1\r\n        # 连接池中的最小空闲连接\r\n        min-idle: 10\r\n        sentinel:\r\n          master: mymaster\r\n          nodes: ${base.config.redis.hostname}:26379,${base.config.redis.hostname}:26380,${base.config.redis.hostname}:26381\r\n\r\ndubbo:\r\n  scan:\r\n    base-packages: com.kevin.cloud.provider.service\r\n  protocol:\r\n    name: dubbo\r\n    port: -1\r\n    serialization: kryo\r\n  registry:\r\n    address: nacos://${base.config.nacos.hostname}:${base.config.nacos.port}?namespace=${base.config.nacos.namespace}\r\n  provider:\r\n    timeout: 5000\r\nredisson:\r\n  master-name: mymaster\r\n  password:\r\n  sentinel-addresses: ${base.config.redis.hostname}:26379,${base.config.redis.hostname}:26380,${base.config.redis.hostname}:26381\r\n  fail-max: 3\r\n  pool-size: 10','9fb7b4127e18233c66e8cbfbe674cfa3','2010-05-05 00:00:00','2020-02-22 14:42:33',NULL,'47.240.91.132','I','270403d1-0aed-4ed7-8643-492a39d9c6d6'),
