(140,157,'service-oauth2-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    tidb:\r\n      hostname: ali.sizegang.cn\r\n      port: 3306\r\n    nacos:\r\n      hostname: 182.92.153.190\r\n      port: 8848\r\n      namespace: 270403d1-0aed-4ed7-8643-492a39d9c6d6\r\n    sentile:\r\n      hostname: localhost\r\n      port: 8020\r\n    redis:\r\n      hostname: www.sizegang.cn\r\n      port: 16379\r\noauth:\r\n  config:\r\n   #  token有效期，设置12小时 默认也是12个小时\r\n    access-token-second: 7200\r\n   \r\n    # refresh_token有效期，设置一周\r\n    refresh-token-second: 604800 \r\n\r\n\r\nspring:\r\n  application:\r\n    name: service-oauth2\r\n  main:\r\n    allow-bean-definition-overriding: true\r\n  jackson:\r\n    time-zone: GMT+8\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n  cloud:\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${base.config.nacos.hostname}:${base.config.nacos.port}\r\n        namespace: fb409926-dcc5-4426-945f-5d1c5cdf3813\r\n      config:\r\n        namespace: fb409926-dcc5-4426-945f-5d1c5cdf3813\r\n    sentinel:\r\n      transport:\r\n        dashboard: ${base.config.sentile.hostname}:${base.config.sentile.port}\r\n  datasource:\r\n    type: com.zaxxer.hikari.HikariDataSource\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    jdbc-url: jdbc:mysql://${base.config.tidb.hostname}:${base.config.tidb.port}/vue_blog?useUnicode=true&characterEncoding=utf-8&serverTimezone=Hongkong&useSSL=false\r\n    username: root\r\n    password: root\r\n    hikari:\r\n      minimum-idle: 5\r\n      idle-timeout: 600000\r\n      maximum-pool-size: 10\r\n      auto-commit: true\r\n      pool-name: MyHikariCP\r\n      max-lifetime: 30000\r\n      connection-timeout: 30000\r\n      connection-test-query: SELECT 1\r\n  redis:\r\n    #单节点配置\r\n    host: ${base.config.redis.hostname}\r\n    port: ${base.config.redis.port}\r\n    #集群配置，生产上使用\r\n    #    cluster:\r\n    #      nodes: ${base.config.redis.hostname}:16379,\r\n    #             ${base.config.redis.hostname}:6391,\r\n    #             ${base.config.redis.hostname}:6392\r\n    #      max-redirects: 3\r\n    #      timeout: 5000\r\n    lettuce:\r\n      pool:\r\n        min-idle: 0\r\n        max-idle: 8\r\n        max-wait: -1ms\r\n        max-active: 8\r\n        #Redis 高可以用集群，如果是基于K8s的方式则不需要配置\r\n        sentinel:\r\n          master: mymaster\r\n          nodes: ${base.config.redis.hostname}:26379,${base.config.redis.hostname}:26380,${base.config.redis.hostname}:26381\r\n\r\ndubbo:\r\n  customer:\r\n    timeout: 5000\r\n  scan:\r\n    base-packages: com.kevin.cloud.service\r\n  protocol:\r\n    name: dubbo\r\n    port: -1\r\n    serialization: kryo\r\n  registry:\r\n    address: nacos://${base.config.nacos.hostname}:${base.config.nacos.port}?namespace=${base.config.nacos.namespace}\r\n\r\nfeign:\r\n  okhttp:\r\n    enabled: true\r\n  httpclient:\r\n    enabled: false\r\n  sentinel:\r\n    enabled: true\r\n\r\nserver:\r\n  port: 9001\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"\r\n\r\nbusiness:\r\n  oauth2:\r\n    grant_type: password\r\n    client_id: client\r\n    client_secret: secret','97ab5f862b7522aaa51c74dd8ad0efdd','2010-05-05 00:00:00','2020-02-22 14:45:17',NULL,'47.240.91.132','U','270403d1-0aed-4ed7-8643-492a39d9c6d6'),
