(0,271,'cloud-gateway-config.yaml','DEFAULT_GROUP','','base:\r\n  config:\r\n    nacos:\r\n      hostname: ali.sizegang.cn\r\n      port: 8848\r\n      namespace: fb409926-dcc5-4426-945f-5d1c5cdf3813\r\n\r\nspring:\r\n\r\n  cloud:\r\n    # 路由网关配置\r\n    gateway:\r\n      # 设置与服务注册发现组件结合，这样可以采用服务名的路由策略\r\n      discovery:\r\n        locator:\r\n          enabled: true\r\n      # 配置路由规则\r\n      routes:\r\n        # 采用自定义路由 ID（有固定用法，不同的 id 有不同的功能，详见：\r\n        #https://cloud.spring.io/spring-cloud-gateway/2.0.x/single/spring-cloud-gateway.html#gateway-route-filters）\r\n        - id: SERVICE-OAUTH2\r\n          # 采用 LoadBalanceClient 方式请求，以 lb:// 开头，后面的是注册在 Nacos 上的服务名\r\n          # 这里的 在nacos中的服务名，最好不要用下划线的服务名，我遇到了这个坑，用下划线找不到\r\n          uri: lb://service-oauth2\r\n          # Predicate 翻译过来是“谓词”的意思，必须，主要作用是匹配用户的请求，有很多种用法\r\n          predicates:\r\n            # 路径匹配，以 api 开头，直接配置是不生效的，看 filters 配置\r\n            - Path=/api/oauth/**\r\n          filters:\r\n            # 前缀过滤，默认配置下，我们的请求路径是 http://localhost:8888/kevin-cloud-oauth2/** 这时会路由到指定的服务\r\n            # 此处配置去掉 1 个路径前缀，再配置上面的 Path=/api/**，就能按照 http://localhost:8888/api/** 的方式访问了\r\n            - StripPrefix=1\r\n        - id: SERVICE-USER\r\n          uri: lb://service-user\r\n          predicates:\r\n            - Path=/api/user/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-UPLOAD\r\n          uri: lb://cloud-upload\r\n          predicates:\r\n            - Path=/api/upload/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: CLOUD-MESSAGE\r\n          uri: lb://cloud-message\r\n          predicates:\r\n            - Path=/api/message/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-BLOG\r\n          uri: lb://service-blog\r\n          predicates:\r\n            - Path=/api/blog/**\r\n          filters:\r\n            - StripPrefix=1\r\n        - id: SERVICE-ARTICLE\r\n          uri: lb://service-article\r\n          predicates:\r\n            - Path=/api/article/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # 使用 Nacos 作为服务注册发现\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${base.config.nacos.hostname}:${base.config.nacos.port}\r\n        namespace: ${base.config.nacos.namespace}\r\n      config:\r\n        namespace: ${base.config.nacos.namespace}\r\n  application:\r\n    # 应用名称\r\n    name: gateway\r\n\r\nserver:\r\n  port: 8888\r\n\r\n# 配置日志级别，方别调试\r\nlogging:\r\n  level:\r\n    org.springframework.cloud.gateway: debug','5053fbe411efa10b2d98c09c5c4c989d','2010-05-05 00:00:00','2020-03-04 15:01:20',NULL,'114.249.19.237','I','48be5b65-6f75-40bc-b8c3-ae4cc74c8f72'),
